<template>
  <div class="warehouse-card-list-wrapper p-2">
    <div class="p-4 bg-white" style="margin-bottom: 10px">
      <BasicForm @register="registerForm" @reset="handleSubmit"/>
    </div>
    <div class="p-2 bg-white">
      <Spin :spinning="state.loading">
        <List
          :grid="{ gutter: 2, xs: 1, sm: 2, md: 4, lg: 4, xl: 5, xxl: 5 }"
          :data-source="data"
          :pagination="paginationProp"
        >
          <template #header>
            <div class="flex items-center justify-between">
              <span class="text-base font-medium pl-2">数据仓列表</span>
              <div class="space-x-2">
                <slot name="header"></slot>
              </div>
            </div>
          </template>
          <template #renderItem="{ item }">
            <ListItem class="p-0">
              <div class="warehouse-card">
                <div class="cover-container">
                  <img
                    :src="item.coverPath || defaultCover"
                    alt="数据仓封面"
                    class="cover-image"
                  />
                  <div class="dataset-count">
                    <span>{{ item.datasets?.length || 0 }} 个数据集</span>
                  </div>
                </div>
                <div class="p-4">
                  <h3 class="text-lg font-semibold mb-2 truncate">{{ item.name }}</h3>
                  <span class="text-gray-600 text-sm line-clamp-2 min-h-[40px]">
                    {{ item.description || '暂无描述' }}
                  </span>
                  <!-- 修改后的按钮区域 -->
                  <div class="btns" style="padding-top: 5px;">
                    <div class="btn" @click="handleView(item)">
                      <img
                        src="data:image/png;base64,"
                        alt="详情"
                      >
                    </div>
                    <div class="btn" @click="handleEdit(item)">
                      <img
                        src="data:image/png;base64,"
                        alt="编辑"
                      >
                    </div>
                    <div class="btn" @click="handleManage(item)">
                      <img
                        src="data:image/png;base64,"
                        alt="管理数据集"
                      >
                    </div>
                    <Popconfirm
                      title="是否确认删除？"
                      ok-text="是"
                      cancel-text="否"
                      @confirm="handleDelete(item)"
                    >
                      <div class="btn">
                        <img
                          src="data:image/png;base64,"
                          alt="删除">
                      </div>
                    </Popconfirm>
                  </div>
                </div>
              </div>
            </ListItem>
          </template>
        </List>
      </Spin>
    </div>
  </div>
</template>

<script lang="ts" setup>
import {onMounted, reactive, ref} from 'vue';
import {List, Popconfirm, Spin} from 'ant-design-vue';
import {BasicForm, useForm} from '@/components/Form';
import {propTypes} from '@/utils/propTypes';
import {isFunction} from '@/utils/is';

const ListItem = List.Item;

defineOptions({ name: 'WarehouseCardList' });

const props = defineProps({
  params: propTypes.object.def({}),
  api: propTypes.func,
});

const emit = defineEmits(['getMethod', 'delete', 'edit', 'view', 'manage']);

const data = ref([]);
const state = reactive({
  loading: true,
});

const defaultCover = 'https://example.com/default-cover.jpg'; // 默认封面图

// 表单
const [registerForm, { validate }] = useForm({
  schemas: [
    {
      field: `name`,
      label: `数据仓名称`,
      component: 'Input',
    },
  ],
  labelWidth: 80,
  baseColProps: { span: 6 },
  autoSubmitOnEnter: true,
  submitFunc: handleSubmit,
});

// 管理数据集
function handleManage(item) {
  emit('manage', item);
}

// 查看详情
function handleView(item) {
  emit('view', item);
}

// 编辑
function handleEdit(item) {
  emit('edit', item);
}

// 删除
function handleDelete(item) {
  emit('delete', item);
}

// 表单提交
async function handleSubmit() {
  const formData = await validate();
  await fetch(formData);
}

// 初始化加载
onMounted(() => {
  fetch();
  emit('getMethod', fetch);
});

async function fetch(p = {}) {
  const { api, params } = props;
  if (api && isFunction(api)) {
    state.loading = true;
    try {
      const res = await api({ ...params, ...p });
      data.value = res.list;
      total.value = res.total;
    } catch (error) {
      console.error(error);
    } finally {
      state.loading = false;
    }
  }
}

// 分页相关
const page = ref(1);
const pageSize = ref(18);
const total = ref(0);
const paginationProp = ref({
  showSizeChanger: true,
  showQuickJumper: true,
  pageSize,
  current: page,
  total,
  showTotal: (total: number) => `共 ${total} 条`,
  onChange: pageChange,
  onShowSizeChange: pageSizeChange,
});

function pageChange(p: number, pz: number) {
  page.value = p;
  pageSize.value = pz;
  fetch();
}

function pageSizeChange(_current, size: number) {
  pageSize.value = size;
  fetch();
}
</script>

